@Composable
fun Base64SvgImage1(base64Svg: String, modifier: Modifier = Modifier) {
    val context = LocalContext.current





// Use Coil to load the byte array into the ImageView




    // Create an ImageLoader with SVG support using Coil 3
    val imageLoader = ImageLoader.Builder(context)
        .components {
            add(SvgDecoder.Factory())
        }.logger(DebugLogger(Logger.Level.Debug))
        .build()


    // Construct a valid Data URI for the SVG
    val dataUri = "data:image/svg+xml;base64,$base64Svg"
    val base64ImageString = dataUri.substringAfter("base64,")

    val imageByteArray = Base64.decode(base64Svg, Base64.DEFAULT)





    // Build the ImageRequest
    val imageRequest = ImageRequest.Builder(context)

        .data(imageByteArray)
        .crossfade(true)
        .build()

    AsyncImage(
        model = imageRequest,
        contentDescription = "SVG Image",
        modifier = modifier,
        imageLoader = imageLoader
    )
}




@Composable
fun Base64SvgImage(base64Svg: String,isSelected:Boolean =false) {
    val context = LocalContext.current






// Use Coil to load the byte array into the ImageView




    // Create an ImageLoader with SVG support using Coil 3
    val imageLoader = ImageLoader.Builder(context)
        .components {
            add(SvgDecoder.Factory())
        }.logger(DebugLogger(Logger.Level.Debug))
        .build()


    // Construct a valid Data URI for the SVG


    val imageByteArray = Base64.decode(base64Svg, Base64.DEFAULT)





    // Build the ImageRequest
    val imageRequest = ImageRequest.Builder(context)

        .data(imageByteArray)
        .crossfade(true)
        .build()

    AsyncImage(
        model = imageRequest,
        contentDescription = "SVG Image",
        modifier = Modifier.clip(CircleShape
        )
            .border(width = 5.dp, shape = CircleShape, color = if(isSelected) Color.Red else Color.Gray)
            .size(96.dp),
        clipToBounds = true,
        contentScale = ContentScale.FillBounds,




        imageLoader = imageLoader
    )
}








