@Composable
fun MakeNewUserProfile(viewModel: ProfileViewModel,navController: NavHostController) {
    val profileState by viewModel.profileState.collectAsState()
    val otp = viewModel.otpCode.value

    Column(
        modifier = Modifier
            .fillMaxSize()
            .padding(16.dp),
        verticalArrangement = Arrangement.Center,
        horizontalAlignment = Alignment.CenterHorizontally
    )
    {
        // Button to trigger profile creation
        Button(onClick = {
            // If OTP is not yet set, first initiate the process to get OTP
            if (otp.isBlank()) {
               // viewModel.initiateProfileCreation(auth ="Bearer $USER_TOKEN")
                // Navigate to the OTP validation screen so the user can enter the OTP


               // viewModel.initiateProfileCreation(auth = "Bearer $USER_TOKEN", name = "mas1", username = "mas2")
              //  viewModel.setProfileData(name = "mass1", username = "mas2")
                navController.navigate(Screen.OtpValidation.route+ "/SubProfile?name=mas?username?test")



            }

        }) {

            Text("Create Profile")

        }

            Spacer(modifier = Modifier.height(16.dp))

            // Display UI based on the current state
            when (profileState) {
                is NetworkResult.Loading -> {
                    CircularProgressIndicator()
                }

                is NetworkResult.Success -> {
                    val profile = (profileState as NetworkResult.Success).data
                    Text(text = "Profile created successfully! Welcome, ${profile?.profileId ?: "User"}")
                }

                is NetworkResult.Error -> {
                    val errorMsg = (profileState as NetworkResult.Error).message
                    Text(text = "Error: $errorMsg", color = MaterialTheme.colors.error)
                }
            }
        }
    }


