@Composable
fun TestHeavyTaskScreen(base64Image: String) {
    var imageBitmap by remember { mutableStateOf<ImageBitmap?>(null) }
    var isLoading by remember { mutableStateOf(true) }

    // Launch the heavy task when base64Image changes (or at first composition)
    LaunchedEffect(key1 = base64Image) {
        isLoading = true
        imageBitmap = performHeavyTask(base64Image)
        isLoading = false
    }

    // Display a loading indicator while the image is being processed.
    if (isLoading) {
        Box(modifier = Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
            CircularProgressIndicator()
        }
    }
    else {

        Column(modifier = Modifier
            .fillMaxSize()
            .background(Color.Green)

        )
        {
            // Show the image if available, otherwise show an error message.
            imageBitmap?.let { bitmap ->
                Image(
                    bitmap = bitmap,
                    contentDescription = "Decoded Image",
                    modifier = Modifier.fillMaxWidth()
                )
            } ?: Text(text = "Error decoding image")

            Text("09099090900090")

        }




    }



}



